# Nebula Graph Environment Variables
# Nebula安装目录 export NEBULA_HOME={{nebula_install_dir}}
# Nebula数据目录 export NEBULA_DATA_DIR={{nebula_data_dir}}
# Nebula日志目录 export NEBULA_LOG_DIR={{nebula_log_dir}}
# Nebula PID目录 export NEBULA_PID_DIR={{nebula_pid_dir}}
# Nebula用户 export NEBULA_USER={{nebula_user}}
# Nebula用户组 export NEBULA_GROUP={{nebula_group}}
# 集群名称 export NEBULA_CLUSTER_NAME={{nebula_cluster_name}}
# Java相关环境变量 export JAVA_HOME={{java_home}} export JAVA_OPTS="-Xmx2g -Xms2g"
# 系统环境变量 export PATH=$NEBULA_HOME/bin:$PATH export LD_LIBRARY_PATH=$NEBULA_HOME/lib:$LD_LIBRARY_PATH
# 创建必要的目录 umask 022
if [ ! -d "$NEBULA_DATA_DIR" ]; then mkdir -p $NEBULA_DATA_DIR chown $NEBULA_USER:$NEBULA_GROUP $NEBULA_DATA_DIR chmod 755 $NEBULA_DATA_DIR fi
if [ ! -d "$NEBULA_LOG_DIR" ]; then mkdir -p $NEBULA_LOG_DIR chown $NEBULA_USER:$NEBULA_GROUP $NEBULA_LOG_DIR chmod 755 $NEBULA_LOG_DIR fi
if [ ! -d "$NEBULA_PID_DIR" ]; then mkdir -p $NEBULA_PID_DIR chown $NEBULA_USER:$NEBULA_GROUP $NEBULA_PID_DIR chmod 755 $NEBULA_PID_DIR fi
